import React, { useState } from 'react';
import axios from 'axios';
import '../../App.css';

// Frappe API details
const FRAPPE_URL = "https://ups-uat.sowaanerp.com";
const USERNAME = "mraza@ups.com";
const PASSWORD = "asadraza@123";

export default function SignUp() {
  // State to hold form input values
  const [formData, setFormData] = useState({
    firstName: '',
    productType: ''
  });

  // Handle input changes and update state
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Function to log in and get session cookies
  const login = async () => {
    try {
      const response = await axios.post(`${FRAPPE_URL}/api/method/login`, {
        usr: USERNAME,
        pwd: PASSWORD
      }, { withCredentials: true });

      if (response.status === 200) {
        console.log("Login successful!");
        return response.headers['set-cookie'];  // Return session cookies
      }
    } catch (error) {
      console.error("Login failed:", error.response ? error.response.data : error.message);
      return null;
    }
  };

  // Handle form submission and post to NextERP API
  const handleSubmit = async (e) => {
    e.preventDefault();

    const cookies = await login(); // Get authentication cookies
    if (!cookies) {
      alert('Login failed! Please check credentials.');
      return;
    }

    try {
      // Lead data to be sent
      const leadData = {
        doctype: "Lead",
        first_name: formData.firstName, // Added first_name
        product_type: formData.productType,
        status: "Open"
      };

      // Sending form data to Frappe API
      const response = await axios.post(
        `${FRAPPE_URL}/api/resource/Lead`,
        leadData,
        {
          headers: {
            Cookie: cookies.join('; '),  // Attach session cookies for authentication
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('Lead created successfully:', response.data);
      alert('Lead created successfully!');

      // Reset form after successful submission
      setFormData({
        firstName: '',
        productType: ''
      });
    } catch (error) {
      console.error('Failed to create lead:', error.response ? error.response.data : error.message);
      alert('There was an error creating the lead. Please try again.');
    }
  };

  return (
    <div className="sign-up-container">
      <h1>Request a Shipment Quote</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="firstName">First Name:</label>
          <input
            type="text"
            id="firstName"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="productType">Product Type:</label>
          <input
            type="text"
            id="productType"
            name="productType"
            value={formData.productType}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="submit-btn">Get Quote</button>
      </form>
    </div>
  );
}
