import React, { useState } from 'react';
import axios from 'axios';
import '../../App.css';

const FRAPPE_URL = "https://ups-uat.sowaanerp.com";  // Replace with your Frappe server URL
const USERNAME = "mraza@ups.com";
const PASSWORD = "asadraza@123";

export default function SignUp() {
  const [formData, setFormData] = useState({
    lead_name: "",
    email_id: "",
    mobile_no: "",
    status: "Open",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // First, authenticate the user
    const isAuthenticated = await login();

    if (!isAuthenticated) {
      console.error("Failed to authenticate. Cannot proceed.");
      return;
    }

    try {
      const response = await axios.post(
        `${FRAPPE_URL}/api/resource/Lead`,
        {
          doctype: "Lead",
          ...formData
        },
        {
          withCredentials: true, // Ensures cookies are sent with the request
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      console.log("Lead created successfully:", response.data);
    } catch (error) {
      console.error("Failed to create lead:", error.response ? error.response.data : error.message);
    }
  };

  const login = async () => {
    try {
      const response = await axios.post(
        `${FRAPPE_URL}/api/method/login`,
        { usr: USERNAME, pwd: PASSWORD },
        { withCredentials: true } // Ensures cookies are stored automatically
      );

      if (response.status === 200) {
        console.log("Login successful!");
        return true;  // Authentication successful
      }
    } catch (error) {
      console.error("Login failed:", error.response ? error.response.data : error.message);
    }
    return false;
  };

  return (
    <div className='sign-up'>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Full Name:</label>
          <input
            type="text"
            name="lead_name"
            value={formData.lead_name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            name="email_id"
            value={formData.email_id}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Mobile Number:</label>
          <input
            type="text"
            name="mobile_no"
            value={formData.mobile_no}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Get Quote</button>
      </form>
    </div>
  );
}
