import React, { useState } from 'react';
import axios from 'axios';
import '../../App.css';

const FRAPPE_URL = "https://ups-uat.sowaanerp.com";  // Replace with your Frappe server URL
const USERNAME = "mraza@ups.com";
const PASSWORD = "asadraza@123";

export default function SignUp() {
  const [formData, setFormData] = useState({
    lead_name: "",
    email_id: "",
    mobile_no: "",
    status: "Open",
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const cookies = await login(); // Get authentication cookies

    if (!cookies) {
      console.error("Failed to authenticate. Cannot proceed.");
      return;
    }

    try {
      const response = await axios.post(
        `${FRAPPE_URL}/api/resource/Lead`,
        formData,
        {
          headers: {
            Cookie: cookies.join('; '),  // Attach session cookies for authentication
            'Content-Type': 'application/json',
          },
        }
      );

      console.log("Lead created successfully:", response.data);
    } catch (error) {
      console.error("Failed to create lead:", error.response ? error.response.data : error.message);
    }
  };

  const login = async () => {
    try {
      const response = await axios.post(`${FRAPPE_URL}/api/method/login`, {
        usr: USERNAME,
        pwd: PASSWORD,
      }, { withCredentials: true });

      if (response.status === 200) {
        console.log("Login successful!");
        return response.headers['set-cookie'];  // Return session cookies
      }
    } catch (error) {
      console.error("Login failed:", error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className='sign-up'>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Full Name:</label>
          <input
            type="text"
            name="lead_name"
            value={formData.lead_name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            name="email_id"
            value={formData.email_id}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Mobile Number:</label>
          <input
            type="text"
            name="mobile_no"
            value={formData.mobile_no}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Get Quote</button>
      </form>
    </div>
  );
}
