import React, { useState } from 'react';
import '../../App.css';

export default function SignUp() {
  // State to hold form input values
  const [formData, setFormData] = useState({
    city: '',
    country: '',
    weight: '',
    productType: '',
    comments: ''
  });

  // Handle input changes and update state
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Handle form submission and post to NextERP API
  const handleSubmit = async (e) => {
    e.preventDefault();

    // API endpoint for creating leads in NextERP (Replace with actual endpoint)
    const apiUrl = 'https://your-nexterp-endpoint.com/api/leads'; 

    try {
      // Sending form data as POST request
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // Add Authorization header if needed (e.g., Bearer token)
          // 'Authorization': 'Bearer your-access-token'
        },
        body: JSON.stringify({
          city: formData.city,
          country: formData.country,
          weight: formData.weight,
          productType: formData.productType,
          comments: formData.comments
        })
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Lead created successfully:', data);
        alert('Lead created successfully!');
        // Reset form after successful submission
        setFormData({
          city: '',
          country: '',
          weight: '',
          productType: '',
          comments: ''
        });
      } else {
        console.error('Error creating lead:', response.statusText);
        alert('There was an error creating the lead. Please try again.');
      }
    } catch (error) {
      console.error('Request failed:', error);
      alert('Request failed. Please check your connection and try again.');
    }
  };

  return (
    <div className="sign-up-container">
      <h1>Request a Shipment Quote</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="city">City:</label>
          <input
            type="text"
            id="city"
            name="city"
            value={formData.city}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="country">Country:</label>
          <input
            type="text"
            id="country"
            name="country"
            value={formData.country}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="weight">Weight (kg):</label>
          <input
            type="number"
            id="weight"
            name="weight"
            value={formData.weight}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="productType">Product Type:</label>
          <input
            type="text"
            id="productType"
            name="productType"
            value={formData.productType}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="comments">Comments:</label>
          <textarea
            id="comments"
            name="comments"
            value={formData.comments}
            onChange={handleChange}
          />
        </div>
        <button type="submit" className="submit-btn">Get Quote</button>
      </form>
    </div>
  );
}
